import{_ as h}from"./ValaxyMain.vue_vue_type_style_index_0_lang-40050c3d.js";import{_,c,w as t,o as u,a as p,b as e,d as s,e as i,r as n,f as m,p as f}from"./app-025bdceb.js";import"./YunFooter.vue_vue_type_script_setup_true_lang-dd4500d1.js";import"./YunCard.vue_vue_type_style_index_0_lang-c0980680.js";import"./YunPageHeader.vue_vue_type_script_setup_true_lang-870a2da2.js";const H=JSON.parse('{"title":"短记：《空之境界》","description":"","frontmatter":{"title":"短记：《空之境界》","categories":["读点什么"],"tags":["杂记","小说","空之境界"],"draft":false,"slug":"54","date":"2021-01-18T22:40:43.000Z","updated":"2021-01-18T22:40:43.000Z"},"headers":[{"level":2,"title":"冷冽而闪烁着微光","slug":"冷冽而闪烁着微光","link":"#冷冽而闪烁着微光","children":[]},{"level":2,"title":"两仪式：因为名叫“爱”的感情重生为人","slug":"两仪式：因为名叫“爱”的感情重生为人","link":"#两仪式：因为名叫“爱”的感情重生为人","children":[]},{"level":2,"title":"黑桐干也：能够填补“空”的爱与善良，最为特别的平凡","slug":"黑桐干也：能够填补“空”的爱与善良，最为特别的平凡","link":"#黑桐干也：能够填补“空”的爱与善良，最为特别的平凡","children":[]},{"level":2,"title":"根源","slug":"根源","link":"#根源","children":[]}],"relativePath":"pages/posts/reading/read_kara_no_kyoukai.md","path":"/home/runner/work/charliedu2000.github.io/charliedu2000.github.io/pages/posts/reading/read_kara_no_kyoukai.md","lastUpdated":1699970883000}'),r=JSON.parse('{"title":"短记：《空之境界》","description":"","frontmatter":{"title":"短记：《空之境界》","categories":["读点什么"],"tags":["杂记","小说","空之境界"],"draft":false,"slug":"54","date":"2021-01-18T22:40:43.000Z","updated":"2021-01-18T22:40:43.000Z"},"headers":[{"level":2,"title":"冷冽而闪烁着微光","slug":"冷冽而闪烁着微光","link":"#冷冽而闪烁着微光","children":[]},{"level":2,"title":"两仪式：因为名叫“爱”的感情重生为人","slug":"两仪式：因为名叫“爱”的感情重生为人","link":"#两仪式：因为名叫“爱”的感情重生为人","children":[]},{"level":2,"title":"黑桐干也：能够填补“空”的爱与善良，最为特别的平凡","slug":"黑桐干也：能够填补“空”的爱与善良，最为特别的平凡","link":"#黑桐干也：能够填补“空”的爱与善良，最为特别的平凡","children":[]},{"level":2,"title":"根源","slug":"根源","link":"#根源","children":[]}],"relativePath":"pages/posts/reading/read_kara_no_kyoukai.md","path":"/home/runner/work/charliedu2000.github.io/charliedu2000.github.io/pages/posts/reading/read_kara_no_kyoukai.md","lastUpdated":1699970883000}'),k={name:"pages/posts/reading/read_kara_no_kyoukai.md",data(){return{data:r,frontmatter:r.frontmatter}},setup(){f("pageData",r)}},g=e("blockquote",null,[e("p",null,"最近的阅读实在潦草，读完这部小说也有好多天了，许多关于书中内容的地方可能不太准确，之后有机会可能会得出更有价值的感想。")],-1),v={id:"冷冽而闪烁着微光",tabindex:"-1"},$=e("p",null,"《空之境界》给人的感觉正如它的名字，它不会像那种现在常见的小说般一上来就仿佛与你关系热络已久、直接把角色的经历如流水账般放给你看，它也并不具备许多引人注目的要素，而是与你保持若即若离的关系（不过，我会有这种感觉可能是因为我是在搞期末复习的时候读完的，心思多少会有些飘忽不定，：D），时刻透露出一种疏离感。",-1),b=e("p",null,"由于最近的阅读量实在太少，我也只能硬着头皮来随便记下我的一些感受。《空之境界》究竟达到了哪种境界？这一点恐怕没有定论，在不同的人群口中肯定会有不同的答案。就我个人看来，它的层次正好是那种“这是一个好故事”的级别。",-1),y=e("p",null,"据说奈须蘑菇本人曾说过，《空之境界》不过是一个boy meets girl的故事。这句话实在恰当。从比较简单的角度理解，《空之境界》就是两仪式与黑桐干也的爱情故事。不难看出，两仪式身上最为关键的转变正是由于黑桐干也，重生也好救赎也罢，都离不开这一层关系。相比之下，对“空”的阐述以及魔术师为了追求根源而展开的争斗反倒不那么重要了。",-1),N=e("p",null,"尽管如此，小说的许多地方仍然显得“帅气”（这个形容来自绫辻行人在《空之境界》上卷末尾的解说），透露出一种特立独行的姿态。这种姿态配合较为利落的描写方法，让我在阅读的时候感觉自己时刻处在清凉的雨中。",-1),T={id:"两仪式：因为名叫“爱”的感情重生为人",tabindex:"-1"},V=e("p",null,"作为两仪家世世代代追求的目标、连接根源的存在，两仪式是极致的“空”，虚无应该是她的存在方式，但是黑桐干也的出现改变了她。",-1),w=e("p",null,"原本的两仪式性格冷酷，她知道自己的异常，但仍然抱着“既然决定要给我，我就会收下”的想法，认为自己会一直过着扭曲而又安稳的生活。而黑桐干也就是那个打破了这个幻想的人，他使两仪式有了对寻常生活（爱）的渴望。对于那个异常的两仪式而言，黑桐干也是让她失控的存在，要解决这个问题，最直接的方法就是解决带来问题的人，也就是说杀死黑桐干也。",-1),Z=e("p",null,"出乎我意料的是，阻止式杀死黑桐的是织，两仪式的第二人格，那个负责“否定”、只会“杀”的人格。为了那个寄托在黑桐身上的梦想，为了式的未来，织选择了自我了断。",-1),B=e("p",null,"从那场车祸存活下来的两仪式在两年之后醒来。醒来的她活得没有实感，只有“死”能让她获得存在的感觉。在这种情况下，也是黑桐干也填补了两仪式的心这座伽蓝之洞。",-1),D=e("p",null,"“人一生只能杀一个人”，为了黑桐干也，两仪式杀死了白纯里绪，背负了本来不该背负的罪孽。然而也是因为黑桐干也，两仪式慢慢融入了琐碎的日常，获得了作为一个人的生活方式。这该说是爱的救赎吗？",-1),S={id:"黑桐干也：能够填补“空”的爱与善良，最为特别的平凡",tabindex:"-1"},C=e("p",null,"黑桐干也这个人怎么说呢？真是“亚撒西”到了极点。几乎对所有人都抱有善意，看见可怜的人就会觉得“不能这样不管”，确实有点像是个滥好人。与此同时，他没有什么特别的期待，总是觉得“像现在这样就好”，像这样的人往往会被称之为“普通人”。但是就算是普通人也会有自己的欲望，黑桐干也显然又不是这样。从一开始就打算平凡的活下去，这才是黑桐干也，这样的他本身反而是极为特别的存在了。“理所当然地活着，理所当然地死去”，这种普通而又特别的孤独的生活方式，就是黑桐干也。他是某种幸福的化身，是普通人无法企及却又触手可及的平凡。",-1),J=e("p",null,"话说回来，这家伙的人缘（尤其是对异性）确实好得过分，搜集情报的能力也让人不得不佩服。",-1),O={id:"根源",tabindex:"-1"},P=e("p",null,"关于“根源”这一概念，我没有什么可说的。人总会追求些什么，不管根源、真实这类东西是否存在，我们都无法停下追寻的脚步，也许只有在这宿命之中发生的事情才能反映出我们的本质。",-1);function U(a,q,A,L,l,M){const o=m,d=h;return u(),c(d,{frontmatter:l.frontmatter,data:l.data},{"main-content-md":t(()=>[g,p(" more "),e("h2",v,[s("冷冽而闪烁着微光 "),i(o,{class:"header-anchor",href:"#冷冽而闪烁着微光","aria-hidden":"true"},{default:t(()=>[s("#")]),_:1})]),$,b,y,N,e("h2",T,[s("两仪式：因为名叫“爱”的感情重生为人 "),i(o,{class:"header-anchor",href:"#两仪式：因为名叫“爱”的感情重生为人","aria-hidden":"true"},{default:t(()=>[s("#")]),_:1})]),V,w,Z,B,D,e("h2",S,[s("黑桐干也：能够填补“空”的爱与善良，最为特别的平凡 "),i(o,{class:"header-anchor",href:"#黑桐干也：能够填补“空”的爱与善良，最为特别的平凡","aria-hidden":"true"},{default:t(()=>[s("#")]),_:1})]),C,J,e("h2",O,[s("根源 "),i(o,{class:"header-anchor",href:"#根源","aria-hidden":"true"},{default:t(()=>[s("#")]),_:1})]),P]),"main-header":t(()=>[n(a.$slots,"main-header")]),"main-header-after":t(()=>[n(a.$slots,"main-header-after")]),"main-nav":t(()=>[n(a.$slots,"main-nav")]),"main-content":t(()=>[n(a.$slots,"main-content")]),"main-content-after":t(()=>[n(a.$slots,"main-content-after")]),"main-nav-before":t(()=>[n(a.$slots,"main-nav-before")]),"main-nav-after":t(()=>[n(a.$slots,"main-nav-after")]),comment:t(()=>[n(a.$slots,"comment")]),footer:t(()=>[n(a.$slots,"footer")]),aside:t(()=>[n(a.$slots,"aside")]),"aside-custom":t(()=>[n(a.$slots,"aside-custom")]),default:t(()=>[n(a.$slots,"default")]),_:3},8,["frontmatter","data"])}const I=_(k,[["render",U]]);export{H as __pageData,I as default};
