import{_ as h}from"./ValaxyMain.vue_vue_type_style_index_0_lang-40050c3d.js";import{_ as u,c,w as t,o as p,a as m,b as e,d as l,e as o,r as n,f as _,p as g}from"./app-025bdceb.js";import"./YunFooter.vue_vue_type_script_setup_true_lang-dd4500d1.js";import"./YunCard.vue_vue_type_style_index_0_lang-c0980680.js";import"./YunPageHeader.vue_vue_type_script_setup_true_lang-870a2da2.js";const I=JSON.parse('{"title":"尝试分析《到官》这首诗","description":"","frontmatter":{"title":"尝试分析《到官》这首诗","tags":["诗歌"],"categories":["读点什么"],"date":"2022-09-14T00:02:39.000Z","updated":"2022-09-14T00:02:39.000Z"},"headers":[{"level":2,"title":"事情是这么来的","slug":"事情是这么来的","link":"#事情是这么来的","children":[]},{"level":2,"title":"尝试直译","slug":"尝试直译","link":"#尝试直译","children":[]},{"level":2,"title":"一些分析","slug":"一些分析","link":"#一些分析","children":[{"level":3,"title":"整首诗的意思或许就是……","slug":"整首诗的意思或许就是","link":"#整首诗的意思或许就是","children":[]}]},{"level":2,"title":"总结","slug":"总结","link":"#总结","children":[]}],"relativePath":"pages/posts/reading/trying-to-analyze-dao-guan.md","path":"/home/runner/work/charliedu2000.github.io/charliedu2000.github.io/pages/posts/reading/trying-to-analyze-dao-guan.md","lastUpdated":1699970883000}'),i=JSON.parse('{"title":"尝试分析《到官》这首诗","description":"","frontmatter":{"title":"尝试分析《到官》这首诗","tags":["诗歌"],"categories":["读点什么"],"date":"2022-09-14T00:02:39.000Z","updated":"2022-09-14T00:02:39.000Z"},"headers":[{"level":2,"title":"事情是这么来的","slug":"事情是这么来的","link":"#事情是这么来的","children":[]},{"level":2,"title":"尝试直译","slug":"尝试直译","link":"#尝试直译","children":[]},{"level":2,"title":"一些分析","slug":"一些分析","link":"#一些分析","children":[{"level":3,"title":"整首诗的意思或许就是……","slug":"整首诗的意思或许就是","link":"#整首诗的意思或许就是","children":[]}]},{"level":2,"title":"总结","slug":"总结","link":"#总结","children":[]}],"relativePath":"pages/posts/reading/trying-to-analyze-dao-guan.md","path":"/home/runner/work/charliedu2000.github.io/charliedu2000.github.io/pages/posts/reading/trying-to-analyze-dao-guan.md","lastUpdated":1699970883000}'),f={name:"pages/posts/reading/trying-to-analyze-dao-guan.md",data(){return{data:i,frontmatter:i.frontmatter}},setup(){g("pageData",i)}},v=e("blockquote",null,[e("p",null,[e("strong",null,"到官")]),e("p",null,"元 范梈"),e("p",null,"陶公到官八十日，今我到官逾半期。"),e("p",null,"劝农省俗了无补，暖衣饱食将何为？"),e("p",null,"常思在山深虎豹，几曾当道假狐狸？"),e("p",null,"英雄见义犹饥渴，可使文人有愧辞？")],-1),k={id:"事情是这么来的",tabindex:"-1"},$=e("p",null,"在秋招的列车上，我找了个风速稍缓的角落坐下来，看起了《银魂》。这个时候，力哥发来这首诗，后面跟着一条信息：有空帮我赏析一下？",-1),b=e("p",null,"虽然大学三年以来实在是荒废了读书（尤指非专业的书）一事，但是能帮得上的那肯定要帮。自己的理解可能有很多不到位的地方，献丑了。",-1),y={id:"尝试直译",tabindex:"-1"},z=e("p",null,"陶公（陶渊明）在官八十余日，如今我上任已过半年。勉励人们耕作、到处视察民俗都无所助益，（我）丰衣足食又是要做什么？常常想到隐于山野中的虎豹，何曾阻拦道路假作狐狸？英雄见到（行）正义之事（的机会）尚且会激动不已，又怎能让文人有虚假的言辞？",-1),N={id:"一些分析",tabindex:"-1"},T=e("p",null,"首联意思比较明显，应该没有太多争议。“陶公”极大可能是指陶渊明，再加上“到官八十日”的描述，应该不会有错。将陶渊明在官八十余日自免去职与自己上任已过半年之事并举，可能是表达自己的处境与陶公类似，借前人故事抒发自己的感受，有欲效仿之意。",-1),V=e("p",null,"根据搜索到的结果，劝农、省俗分别指勉励人们耕作、视察民情。陶渊明亦有《劝农》诗劝勉人们重视和从事农业劳动；省俗观风，应该为视察民俗民风之意。“无补”可能是诗人认为自己在官半年多做的这些事只是表面功夫，并没有真的为百姓做实事。“暖衣饱食”即穿得暖吃得饱，类似于丰衣足食，“将何为”应该是诗人反问自己，即使自己丰衣足食又有何作为。（如果按“即使百姓能丰衣足食又有什么用”来理解的话感觉不是很搭。首先百姓“暖衣饱食”就不至于质问“何为”，其次既然认为自己在任没有做实事，拿百姓“暖衣饱食”来承接就显得矛盾。）",-1),w=e("p",null,"“在山深虎豹”从字面意义上讲应该是隐于深山的虎豹，我认为可以引申为隐退于市井的有志之士。“当道”意为阻挡道路，也指身居要位，“假狐狸”直译可能是假借狐狸的身份，与“虎豹”对应的话可能是说充当仗势欺人的小人。这一联应该是指自己宁愿坚守气节归隐山林，也不愿继续狐假虎威无所作为。",-1),Z=e("p",null,"“英雄见义”自然要勇为，“饥渴”可能是强调“见义”后正义感的冲动。“愧”一般指使人羞惭之意，在这里“愧辞”应该是说不真实（从而使自己羞惭）的言辞（或者从《左传·襄公二十七年》：“夫子之家事治,言於晋国无隐情，其祝史陈信於鬼神无愧辞。”等处可以看出，“愧辞”二字可直接译为不真实的言辞）。表达自己不愿屈从、坚持表达真实想法之意。",-1),B={id:"整首诗的意思或许就是",tabindex:"-1"},D=e("p",null,"陶渊明上任仅仅八十余日就弃官归隐，而我现在就职半年有余，心中有效仿陶公之意。我在这里仅仅是劝勉人们辛勤耕作、到处视察民风民俗，逞尽官威，却对百姓的生活毫无助益。这样的话，即使我吃饱穿暖了又能有何作为？我常常想着宁愿怀抱自己的气节志向归隐山林，何曾想过要像现在这样尸位素餐？那些英雄人物见到行正义之事的机会都会想着积极行动，我作为文人又怎么能违背自己的本心？",-1),S=e("p",null,[e("s",null,"看不惯官场的现状，我不做官啦！大概这样？")],-1),A={id:"总结",tabindex:"-1"},C=e("p",null,"以上算是我个人的思路。对于这种比较冷门的诗文，直接上网查资料确实比我高中的时候翻书要方便些（虽然网上除了原文和作者简介之外就没有任何直接的分析了，这也太冷门了）。",-1),J=e("p",null,"希望不会有太离谱的错误，如果有的话请务必指出！谢谢大伙儿！",-1),O=e("p",null,"😄",-1);function P(a,U,q,G,r,L){const s=_,d=h;return p(),c(d,{frontmatter:r.frontmatter,data:r.data},{"main-content-md":t(()=>[v,m("more"),e("h2",k,[l("事情是这么来的 "),o(s,{class:"header-anchor",href:"#事情是这么来的","aria-hidden":"true"},{default:t(()=>[l("#")]),_:1})]),$,b,e("h2",y,[l("尝试直译 "),o(s,{class:"header-anchor",href:"#尝试直译","aria-hidden":"true"},{default:t(()=>[l("#")]),_:1})]),z,e("h2",N,[l("一些分析 "),o(s,{class:"header-anchor",href:"#一些分析","aria-hidden":"true"},{default:t(()=>[l("#")]),_:1})]),T,V,w,Z,e("h3",B,[l("整首诗的意思或许就是…… "),o(s,{class:"header-anchor",href:"#整首诗的意思或许就是","aria-hidden":"true"},{default:t(()=>[l("#")]),_:1})]),D,S,e("h2",A,[l("总结 "),o(s,{class:"header-anchor",href:"#总结","aria-hidden":"true"},{default:t(()=>[l("#")]),_:1})]),C,J,O]),"main-header":t(()=>[n(a.$slots,"main-header")]),"main-header-after":t(()=>[n(a.$slots,"main-header-after")]),"main-nav":t(()=>[n(a.$slots,"main-nav")]),"main-content":t(()=>[n(a.$slots,"main-content")]),"main-content-after":t(()=>[n(a.$slots,"main-content-after")]),"main-nav-before":t(()=>[n(a.$slots,"main-nav-before")]),"main-nav-after":t(()=>[n(a.$slots,"main-nav-after")]),comment:t(()=>[n(a.$slots,"comment")]),footer:t(()=>[n(a.$slots,"footer")]),aside:t(()=>[n(a.$slots,"aside")]),"aside-custom":t(()=>[n(a.$slots,"aside-custom")]),default:t(()=>[n(a.$slots,"default")]),_:3},8,["frontmatter","data"])}const K=u(f,[["render",P]]);export{I as __pageData,K as default};
